import pandas as pd
import json

notes = pd.read_csv("../input/nbme-score-clinical-patient-notes/patient_notes.csv")

train_notes = notes[notes.case_num != 0].sample(n=5000)
val_notes = notes[notes.case_num == 0].sample(n=500)

with open("train.json", "w") as fp:
    for note in train_notes.pn_history:
        fp.write(json.dumps({"text":note})+'\n')

with open("val.json", "w") as fp:
    for note in val_notes.pn_history:
        fp.write(json.dumps({"text":note})+'\n')


# In[ ]:


get_ipython().system('python run_mlm.py --model_name_or_path "microsoft/deberta-base" --num_train_epochs 1 --train_file "train.json" --validation_file "val.json" --per_device_train_batch_size 4 --per_device_eval_batch_size 8 --max_seq_length 384 --do_train --do_eval --output_dir "deb-base-regular" --overwrite_output_dir --save_strategy "no" --logging_steps 200 --learning_rate 1e-5 --report_to "none"')


